for e,i in enumerate(a):
    if i == '(':
        floor+=1
    elif i == ')':
        floor-=1
    else:
        print('error - character not in ["(",")"] ')
        break
    if floor == -1:
        print(floor)
        print('goes to -1 at step ' +  str(e + 1))
        break


